import React, { useState } from "react";

export default function Textform(props) {
  const handleUpClick = () => {
    // console.log("Uppercase Button Clicked" + text);
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleLwClick = () => {
    // console.log("Uppercase Button Clicked" + text);
    let newText = text.toLowerCase();
    setText(newText);
  };

  const handleClearClick = () => {
    // console.log("Uppercase Button Clicked" + text);
    let newText = '';
    setText(newText);
  };

  const handleOnChange = (event) => {
    // console.log("OnChange Button Clicked");
    setText(event.target.value);
  };

  const [text, setText] = useState("");
  return (
    <>
    <div className="container">
      <h1>{props.heading}</h1>

      <div className="mb-3">
        <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
      </div>

      <button className="btn btn-primary btn-sm mx-1" onClick={handleUpClick}>
        Convert UpperCase
      </button>

      <button className="btn btn-primary btn-sm mx-1" onClick={handleLwClick}>
        Convert LowerCase
      </button>

      <button className="btn btn-primary btn-sm mx-1" onClick={handleClearClick}>
        Clear Text
      </button>

      <button className="btn btn-primary btn-sm mx-1" onClick={handleCopyrClick}>
        Clear Text
      </button>

    </div>

    <div className="container my-4">
        <h2>Your Text Summary</h2>
        <p>{text.split(" ").length-1} words and {text.length} characters</p>
        <p>{0.008 * text.split(" ").length} Minutes to read</p>
        <h3>Preview</h3>
        <p>{text}</p>
    </div>

    </>
  );
}
