import React, { useState } from 'react';

export default function Textform(props) {

    const handleUpClick = () => {
        console.log("Uppercase Button Clicked" + text);
        let newText = text.
        setText("Your text is converted to uppercse")
    }
    const handleOnChange = (event) => {
        console.log("OnChange Button Clicked");
        setText(event.target.value)
    }
    const [text, setText] = useState ("Enter your text")
    return (
        <div>
            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="12"></textarea>
            </div>
            <button className="btn btn-primary" onClick={handleUpClick}>Convert UpperCase</button>
        </div>
    )
}
